import { useRef, useState } from "react";
import React from "react";
import { useNavigate } from 'react-router-dom';
import { Link } from "react-router-dom";

function Registration() {
  const navigate = useNavigate();
  const [termsAccepted, setTermsAccepted] = useState(false);

  const [userData, setUserData] = useState({
    Name: "",
    uEmail: "",
    uName: "",
    uPassword1: "", 
    uPassword2: ""  
  });

  const [errors, setErrors] = useState({
    nameErr: "",
    emailErr: "",
    userNameErr: "",
    passwordErr: "",
    confirmPasswordErr: ""
  });

  const [alert, setAlert] = useState({
    show: false,
    message: "",
    type: ""
  });

  function ChangeUserData(e) {
    if (e.target.name === 'Name') {
      setUserData({
        ...userData,
        Name: e.target.value
      });

      setErrors({
        ...errors,
        nameErr: e.target.value.trim() === "" ? "Name is required" : ""
      });

    } else if (e.target.name === 'uEmail') { 
      setUserData({
        ...userData,
        uEmail: e.target.value
      });

      const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      setErrors({
        ...errors,
        emailErr: !emailPattern.test(e.target.value) ? "Please Enter a Valid Email" : ""
      });

    } else if (e.target.name === 'uPassword1') { 
      setUserData({
        ...userData,
        uPassword1: e.target.value
      });

      const passwordPattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{8,}$/;
      setErrors({
        ...errors,
        passwordErr: !passwordPattern.test(e.target.value) ? "Password must be at least 8 characters and contain an uppercase letter, a lowercase letter, and a digit" : ""
      });

    } else if (e.target.name === 'uPassword2') { 
      setUserData({
        ...userData,
        uPassword2: e.target.value
      });

      setErrors({
        ...errors,
        confirmPasswordErr: e.target.value !== userData.uPassword1 ? "Passwords do not match" : ""
      });
    }
  }

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleToggleConfirmPassword = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  function handleSignUp(e) {
    e.preventDefault();

    // Check for empty fields
    if (userData.Name.trim() === "" || userData.uEmail.trim() === "" || userData.uPassword1.trim() === "" || userData.uPassword2.trim() === "") {
      setAlert({
        show: true,
        message: "Please fill all the fields",
        type: "error"
      });
      return;
    }

    // Check for errors
    if (errors.nameErr || errors.emailErr || errors.userNameErr || errors.passwordErr || errors.confirmPasswordErr) {
      setAlert({
        show: true,
        message: "Please fix the errors in the form",
        type: "error"
      });
      return;
    }

    // Check if terms are accepted
    if (!termsAccepted) {
      setAlert({
        show: true,
        message: "You must accept the Terms and Conditions",
        type: "error"
      });
      return;
    }

    // Clear alert message
    setAlert({
      show: false,
      message: "",
      type: ""
    });

    // Save user data to localStorage and navigate to login page
    localStorage.setItem("isLoggedIn", "true");
    localStorage.setItem("userName", userData.uName);
    localStorage.setItem("userEmail", userData.uEmail);
    localStorage.setItem("userPassword", userData.uPassword1);

    navigate("/Login");
  }



  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md bg-white p-8 border border-gray-300 rounded-lg shadow-lg">
        {alert.show && (
          <div className={`p-4 mb-4 text-sm ${alert.type === "error" ? "bg-red-100 text-red-600" : "bg-green-100 text-green-600"} rounded-lg`} role="alert">
            {alert.message}
          </div>
        )}

        <form onSubmit={handleSignUp}>
          <div className="mb-8">
            <h3 className="text-gray-800 text-2xl font-bold">Register</h3>
          </div>

          <div className="space-y-6">
            <div>
              <label className="text-gray-800 text-sm mb-2 block">Name</label>
              <div className="relative flex items-center">
                <input
                  name="Name"
                  type="text"
                  required
                  className="bg-white border border-gray-300 w-full text-sm text-gray-800 pl-4 pr-10 py-2.5 rounded-md outline-blue-500"
                  placeholder="Enter name"
                  value={userData.Name}
                  onChange={ChangeUserData}
                />
                {errors.nameErr && <p className="text-sm text-red-600 absolute left-0 bottom-0 w-full h-0 bg-red-300">{errors.nameErr}</p>}
              </div>
            </div>

            <div>
              <label className="text-gray-800 text-sm mb-2 block">Email</label>
              <div className="relative flex items-center">
                <input
                  name="uEmail" 
                  type="email"
                  required
                  className="bg-white border border-gray-300 w-full text-sm text-gray-800 pl-4 pr-10 py-2.5 rounded-md outline-blue-500"
                  placeholder="Enter email"
                  value={userData.uEmail}
                  onChange={ChangeUserData}
                />
                {errors.emailErr && <p className="text-sm text-red-600 absolute left-0 bottom-0 w-full h-0 bg-red-300">{errors.emailErr}</p>}
              </div>
            </div>

            <div>
              <label className="text-gray-800 text-sm mb-2 block">Password</label>
              <div className="relative flex items-center">
                <input
                  name="uPassword1" 
                  type={showPassword ? "text" : "password"}
                  required
                  className="bg-white border border-gray-300 w-full text-sm text-gray-800 pl-4 pr-10 py-2.5 rounded-md outline-blue-500"
                  placeholder="Enter password"
                  value={userData.uPassword1}
                  onChange={ChangeUserData}
                />
                {errors.passwordErr && <p className="text-sm text-red-600 absolute left-0 bottom-0 w-full h-0 bg-red-300">{errors.passwordErr}</p>}
                <button type="button" onClick={handleTogglePassword} className="absolute right-4">
                  {showPassword ? "Hide" : "Show"}
                </button>
              </div>
            </div>

            <div>
              <label className="text-gray-800 text-sm mb-2 block mt-11">Confirm Password</label>
              <div className="relative flex items-center">
                <input
                  name="uPassword2" 
                  type={showConfirmPassword ? "text" : "password"}
                  required
                  className="bg-white border border-gray-300 w-full text-sm text-gray-800 pl-4 pr-10 py-2.5 rounded-md outline-blue-500 mt-1"
                  placeholder="Retype password"
                  value={userData.uPassword2}
                  onChange={ChangeUserData}
                />
                {errors.confirmPasswordErr && <p className="text-sm text-red-600 absolute left-0 bottom-0 w-full h-0 bg-red-300">{errors.confirmPasswordErr}</p>}
                <button type="button" onClick={handleToggleConfirmPassword} className="absolute right-4">
                  {showConfirmPassword ? "Hide" : "Show"}
                </button>
              </div>
            </div>

            <div className="flex items-center">
            <input
  id="remember-me"
  name="remember-me"
  type="checkbox"
  checked={termsAccepted}
  onChange={(e) => setTermsAccepted(e.target.checked)}
  className="h-4 w-4 shrink-0 text-blue-600 focus:ring-blue-500 border-gray-300 rounded-md"
/>

              <label htmlFor="remember-me" className="text-gray-800 ml-3 block text-sm">
                I accept the{" "}
                <a href="javascript:void(0);" className="text-blue-600 font-semibold hover:underline ml-1">
                  Terms and Conditions
                </a>
              </label>
            </div>
          </div>

          <div className="!mt-8">
            <button
              type="submit"
              className="w-full py-2.5 px-4 text-sm tracking-wider font-semibold rounded-md bg-blue-600 hover:bg-blue-700 text-white focus:outline-none"
            >
              Create Account
            </button>
          </div>
          <p className="text-gray-800 text-sm mt-6 text-center">
            Already have an account?{" "}
            <Link to="/Login" className="font-medium text-primary-600 hover:underline">
              Sign in
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
}

export default Registration;
